import {
    Button,
    Card,
    Form,
    FormLayout,
    Layout,
    Page,
    SettingToggle,
    Stack,
    TextField,
    TextStyle,
    Heading
  } from '@shopify/polaris';
  import React from 'react';
import {AppProvider, ContextualSaveBar, Frame} from '@shopify/polaris';

  export default class AnnotatedLayout extends React.Component {
    render() {
      return (
        <page>
          <Heading>Online store dashboard</Heading>
        <div style={{height: '250px'}}>
    <AppProvider

      i18n={{
        Polaris: {
          Frame: {
            skipToContent: 'Skip to content',
          },
          ContextualSaveBar: {
            save: 'Save',
            discard: 'Discard',
          },
        },
      }}
    >
      <Frame>
        <ContextualSaveBar

          saveAction={{
            onAction: () => console.log('add form submit logic'),
            loading: false,
            disabled: false,
          }}
          discardAction={{
            onAction: () => console.log('add clear form logic'),
          }}
        />
      </Frame>
    </AppProvider>
  </div>
  </page>
      );
    }


  }
