import {
	AppProvider,
	Button,
	ButtonGroup,
    RadioButton,
	Card,
	Frame,
    Form,
    FormLayout,
    Layout,
    Page,
    SettingToggle,
    Stack,
    TextField,
    TextStyle,
    Heading,
    Subheading,
	RangeSlider,
	Collapsible,
	DatePicker,
	Select,
	Thumbnail
  } from '@shopify/polaris';
import React, { Component, useCallback, useState } from 'react';
import '@shopify/polaris/styles.css';
import TabsExample from './TabsExample';
import Summary from './sections/Summary';
import Stakeholders from './sections/Stakeholders';
import Dates from './sections/Dates';

export default class index extends Component {
	constructor(props) {
		super(props);
		this.state = {
			summaryq1: "Hi"
		}
	}


	render() {





		//	onChange = (e) => this.setState({ summaryq1: 'test' });

		// const [selected, setSelected] = useState(0);

		// const handleTabChange = useCallback(
		//	(selectedTabIndex) => setSelected(selectedTabIndex),
		//	[],
		//  );

		  const tabs = [
			{
			  id: 'all-customers',
			  content: 'All',
			  accessibilityLabel: 'All customers',
			  panelID: 'all-customers-content',
			},
			{
			  id: 'accepts-marketing',
			  content: 'Accepts marketing',
			  panelID: 'accepts-marketing-content',
			},
			{
			  id: 'repeat-customers',
			  content: 'Repeat customers',
			  panelID: 'repeat-customers-content',
			},
			{
			  id: 'prospects',
			  content: 'Prospects',
			  panelID: 'prospects-content',
			},
		  ];


      return (

			<AppProvider i18n={{}}
			features={{newDesignLanguage: true}}
			theme={{
			colors: {
				surface: '#111213',
				onSurface: '#111213',
				interactive: '#2e72d2',
				secondary: '#111213',
				primary: '#3b5998',
				critical: '#d82c0d',
				warning: '#ffc453',
				highlight: '#5bcdda',
				success: '#008060',
				decorative: '#ffc96b',
			},
			}}>

				<Frame>
					<Page title="Launch Accelerator" thumbnail={<Thumbnail source="https://screenshot.click/20-06-xy1ym-xqg8n.png"/>}>
						<Layout>
							<Layout.Section>
								<ButtonGroup>
									<Button>Summary</Button>
									<Button>Stakeholders</Button>
									<Button>Dates</Button>
									<Button>Migration</Button>
									<Button>Design</Button>
									<Button>Apps</Button>
									<Button>Products</Button>
									<Button>Payments</Button>
									<Button>Taxes</Button>
									<Button>Languages</Button>
									<Button>Channels</Button>
									<Button>Objectives</Button>
									<Button>Additional</Button>
								</ButtonGroup>
								<TabsExample/>
								<FormLayout>


								<Summary/>
								<Stakeholders/>
								<Dates/>




								</FormLayout>
							</Layout.Section>
						</Layout>
					</Page>
				</Frame>

			</AppProvider>

      );
    }

  }
