import {
    Button,
    RadioButton,
    Card,
    Form,
    FormLayout,
    Layout,
    Page,
    SettingToggle,
    Stack,
    TextField,
    TextStyle,
    Heading,
    Subheading
  } from '@shopify/polaris';
  import React from 'react';
import {AppProvider, ContextualSaveBar, Frame} from '@shopify/polaris';

  export default class AnnotatedLayout extends React.Component {
    render() {
      return (
        <page>
<Layout>
  <Layout.Section>
    <Card title="Business Summary" sectioned></Card><br></br>
    <FormLayout>
  <TextField label="1. Tell us about your business: your brand, industry, products and customers."/>
  <TextField label="2. What is your annual business revenue to date?"/>
  <Stack vertical>
      <RadioButton
        label="Accounts are disabled"
        helpText="Customers will only be able to check out as guests."
        checked={value === 'disabled'}
        id="disabled"
        name="accounts"
        onChange={handleChange}
      />
      <RadioButton
        label="Accounts are optional"
        helpText="Customers will be able to check out with a customer account or as a guest."
        id="optional"
        name="accounts"
        checked={value === 'optional'}
        onChange={handleChange}
      />
    </Stack>
  </FormLayout>
  </Layout.Section>
</Layout>
  </page>
      );
    }


  }
