import React, {Component, useCallback, useState} from 'react';
import {
	Layout,
	Card,
	TextField,
	Stack,
	RadioButton
} from '@shopify/polaris';
import '@shopify/polaris/styles.css';

export default function Summary() {
	const [summaryq1, setValue] = useState('Question 1');
	const [summaryq2] = useState('Question 2');
	const [summaryq3] = useState('false');
	const [summaryq4] = useState('Question 4');
	const [summaryq5] = useState('Question 5');
	const onChange = useCallback((newValue) => setValue(newValue), []);
		return (
			<Layout>
				<Layout.AnnotatedSection
					title="Business summary"
					description="Please fill out as much of this form as you can. This will help us to help you.">
					<Card sectioned>
						<TextField name="summaryq1" 
						onChange={onChange}

							label="Tell us about your business: your brand, industry, products and customers." 
						value={summaryq1}
						/>
						<TextField label="What is your annual business revenue to date?" value={summaryq2} onChange={onChange}/>
						Do you have other brands currently on Shopify or Shopify Plus?
						<Stack horizontal>
							<RadioButton
								label="Yes"
								id="disabled"
								name="accounts"
							/>
							<RadioButton
								label="No"
								id="optional"
								name="accounts"
							/>
						</Stack>
						<TextField label="If yes, then please provide the URL:"/>
						<TextField label="What are your main reasons for choosing Shopify Plus?"/>
					</Card>
				</Layout.AnnotatedSection>
			</Layout>
		)
}
