import React, { useCallback, useState } from "react";
import {
  Button,
  Card,
  Collapsible,
  TextContainer,
  ButtonGroup,
  Scrollable
} from "@shopify/polaris";
import '@shopify/polaris/styles.css'

export default function CollapsibleExample() {
  const [active, setActive] = useState(true);

  //  const handleToggle = useCallback(() => setActive(active => !active), []);
  function handleToggle(id) {
    const obj = document.getElementById(id).setState('false');
  }

  return (
    <div style={{ height: "200px" }}>
      <Card sectioned>
        <Scrollable shadow style={{ height: "1000px" }}>
          <ButtonGroup segmented>
            <Button
              id="Summary-tab-button"
              onClick={handleToggle("Summary-tab")}
              ariaExpanded={active}
              ariaControls="Summary-tab"
            >
              Summary
            </Button>
            <Button
              onClick={handleToggle}
              ariaExpanded={active}
              ariaControls="Stakeholders-tab"
            >
              Stakeholders
            </Button>
            <Button>Summary</Button>
            <Button>Dates</Button>
            <Button>Migration</Button>
            <Button>Design</Button>
            <Button>Apps</Button>
            <Button>Products</Button>
            <Button>Payments</Button>
            <Button>Taxes</Button>
            <Button>Languages</Button>
            <Button>Channels</Button>
            <Button>Objectives</Button>
            <Button>Additional</Button>
          </ButtonGroup>
          <Collapsible
            open={active}
            id="Summary-tab"
            transition={{ duration: "150ms", timingFunction: "ease" }}
          >
            <TextContainer>
              Summary Info - Your mailing list lets you contact customers or
              visitors who have shown an interest in your store. Reach out to
              them with exclusive offers or updates about your products.
            </TextContainer>
          </Collapsible>
          <Collapsible
            open={active}
            id="Stakeholders-tab"
            transition={{ duration: "150ms", timingFunction: "ease" }}
          >
            <TextContainer>
              Stakeholders info - Your mailing list lets you contact customers
              or visitors who have shown an interest in your store. Reach out to
              them with exclusive offers or updates about your products.
            </TextContainer>
          </Collapsible>
        </Scrollable>
      </Card>
    </div>
  );
}
